import groovy.json.JsonSlurper
def Data = context.testCase.getTestStepByName("User login").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(Data)
def token = holder.accessToken

//Setting a varible in project level with the value
context.testCase.TestSuite.project.setPropertyValue("AT","bearer "+token)

import groovy.json.JsonSlurper
def san = context.testCase.getTestStepByName("Get all users").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(san)
def num = holder.count.toInteger()
for(def i=0;i<num;i++)
{
	if(holder.users[i].email=="sunil666@gmail.com")
	{
		def uid = holder.users[i]._id
		//log.info uid
		def converted_uid = uid.toString()
		context.testCase.testSuite.project.setPropertyValue("uid",converted_uid)
		log.info converted_uid
	}
}

import groovy.json.JsonSlurper
def san = context.testCase.getTestStepByName("Get all products").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(san)
def num = holder.count.toInteger()
for(def i=0;i<num;i++)
{
	if(holder.products[i].name=="hp laptop i5")
	{
		def pid = holder.products[i]._id
		//log.info pid
		def converted_pid = pid.toString()
		context.testCase.testSuite.project.setPropertyValue("Proid",converted_pid)
		log.info converted_pid
	}
}

import groovy.json.JsonSlurper
def san = context.testCase.getTestStepByName("Get all orders").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(san)
def num = holder.count.toInteger()
for(def i=0;i<num;i++)
{
	if(holder.orders[i].product!=="null")
	{
		def oid = holder.orders[i]._id
		//log.info oid
		def converted_oid = oid.toString()
		context.testCase.testSuite.project.setPropertyValue("oid",converted_oid)
		log.info converted_oid
	}
}
